var TableArray = [];
for( i=0; i < msg.payload.response.route[0].leg[0].maneuver.length; i++ ) {
      var TableEntry = {};
      // strip out HTML
      TableEntry.instruction = msg.payload.response.route[0].leg[0].maneuver[i].instruction.replace(/<[^>]*>?/gm, '') ;
      TableEntry.latitude = msg.payload.response.route[0].leg[0].maneuver[i].position.latitude ;
      TableEntry.longitude = msg.payload.response.route[0].leg[0].maneuver[i].position.longitude ;
      TableArray.push( TableEntry );
}
flow.set("Steps",TableArray);

msg.payload = TableArray;
return msg;

var route = flow.get("Steps") || [];
var step  = flow.get("current") || 0;
var msg1 = {};
msg.payload = {};

if( step < route.length ) {
    msg.payload.name = "route";
    msg.payload.layer = "route";
    msg.payload.lat = route[step].latitude;
    msg.payload.lon = route[step].longitude;
    msg.payload.icon = "fa-car";
    msg.payload.iconColor = "maroon"
    msg1.instructions = route[step].instruction;
    flow.set("current", step+1 );
} else {
    msg1.instructions = "You have arrived!";
    flow.set("current", 0 );
}

return [msg,msg1];

